INSERT INTO `script_texts` (`entry` ,`content_default`, `type`, `comment`)VALUES ('-2100019',  'Begone, uncouth scum! The Alliance shall prevail in Alterac Valley!', '1', 'BALINDA_YELL_AGGRO');
